import styled from 'styled-components'

export const Container = styled.div`

  background: #0d1117;

`

export const ContainerProf = styled.div`

  max-width: 1280px;
  margin: 0 auto;

`

export const ContainerHeaderProfile = styled.div`

  max-width: 19.5rem;
  padding: 1rem 1rem;

`

export const HeaderProfile = styled.div`
  color: white;
  max-width: 17.5rem;
  margin: 0 auto;

  img {
    width: 100%;
    clip-path: circle();
  }

  h2 {
    font-size: 1.75rem;
  }
  h3 {
    margin-top: -1.5rem;
    color: #8b949e;
    font-size: 1.25rem;
    font-weight: normal;
  }

  a {
    background: #21262d;
    font-size: 0.9375rem;
    color: #c9d1d9;
    text-decoration: none;
    text-align: center;
    display: inline-block;
    width: 100%;
    padding: 0.5rem;
    border-radius: .4rem;
    border: 1px solid grey;
    margin-bottom: 1rem;
  }

`
export const InsightProfile = styled.div`

  color: #8b949e;
  font-size: 0.9375rem;
  padding: 0 1rem 1rem 1rem;
  border-bottom: 1px solid #c9d1d9;

  span {
    margin-right: .5rem;

  }

`

export const AchievementsProfile = styled.div`

  padding: 0 1rem;
  color: white;

  img {
    max-width: 64px;
  }

`
