import styled from 'styled-components'

export const ContainerSubNav = styled.div`
  padding-top: 1.75rem;
  background: #0d1117;
`

export const ContainerSub = styled.div`

  max-width: 1280px;
  margin: 0 auto;
  color: white;

  display: flex;
  align-items: center;

  div {
    width: 312px;
  }

  nav {
    padding: 0 0rem;
  }
  ul {
    list-style: none;
    display: flex;
    align-items: center;

    li {
      color: #c9d1d9;
      font-size: 0.9375rem;
    }
    &:hover {
        border-bottom: 2px solid red;
    }
  }

`
