import styled from 'styled-components'

export const Container = styled.div`

  max-width: 320px;
  margin: 0 auto;
  padding: 2rem 0rem;
  text-align: center;

  img {
    padding: 1rem 0;
  }

  h2 {
    font-weight: normal;
    font-size: 1.75rem;
    color: #ffffff;
    margin-bottom: 0.9375rem;


  form {
    display: flex;
    flex-direction: column;
    background: rgba(4,0=5,5,0.5);
    padding: 1rem;

    label {
      text-align: start;
      color: rgba(256, 256, 256, 0.7);
      font-size: 0.75rem;
      margin-bottom: 0.675rem;
    }
    input {
      margin: .375rem 0 0.9375rem 0;
      padding: .375rem;
    }
  }

`
