import styled from 'styled-components'

export const Container = styled.div`

  max-width: 320px;
  margin: 0 auto;
  padding: 2rem 0;
  text-align: center;

  img {
    padding: 1rem 0;
  }

  h2 {
    font-weight: normal;
    font-size: 1.75rem;
    color: #ffffff;
    margin-bottom: 0.9375rem;
  }

  form {
    display: flex;
    flex-direction: column;

    label {
      text-align: start;
      color: rgba(256, 256, 256, 0.2);
    }
  }

`
